import { useState } from "react";


export function BillCalculator(selectedProfile) {
  const [bill, setBill] = useState(0);
  const [expense, setExpense] = useState(0);
  // const [person, setPerson] = useState("user");

  const billing = bill - expense;

  function handleSubmit(e) {
    e.preventDefault();
    const newBill = { bill, expense, billing };
    console.log(newBill);
    setBill(0);
    setExpense(0);
  }

  return (
    <form className="form-split-bill" onSubmit={handleSubmit}>
      <h2>Split A Bill with {selectedProfile.name} </h2>
      
      <label>üí∞Bill value</label>
      <input
        type="text"
        value={bill}
        onChange={(e) => setBill(e.target.value)}
      />

      <label>üßî‚Äç‚ôÇÔ∏èYour Expenses</label>
      <input
        type="text"
        value={expense}
        onChange={(e) => setExpense(e.target.value)}
      />

      <label>üßë‚Äçü§ù‚Äçüë©üèª{sele}Expenses</label>
      <input type="text" disabled value={billing} />

      <label>ü§ë Who is paying the Bill</label>
      <select
      // onChange={(e)=> setPerson(e.target.value)}
      >
        <option value="user">You</option>
        <option value="friend">{selectedProfile.name}</option>
      </select>
      <button className="button">Split Bill</button>
    </form>
  );
}
